mapper.select:
SELECT t1.id CODE, t1.roomImgIcon, t1.NAME, ct1.tableNumber, t1.STATUS, t1.peopleMaxNum, t1.type, t1.createTime createDate, t1.startTime, (case when t1.endTime is null then now() else t1.endTime end) as endDate, (case when t1.chatRoomEndTime is null then now() else t1.chatRoomEndTime end) as chatRoomEndTime, count( ct0.id ) peopleNum FROM ddw_goodfriendplay_room t1 LEFT JOIN ddw_goodfriendplay_tables ct1 ON ct1.id = t1.tableCode LEFT JOIN ddw_goodfriendplay_room_member ct0 ON ct0.roomId = t1.id
and t1.centerId=#{centerId}
and t1.disabled=#{disabled}
and t1.status${status}
and t1.type=#{type}
and t1.roomOwner=#{roomOwner}
GROUP BY t1.id ORDER BY t1.status Desc,t1.updateTime Desc,peopleNum Desc
limit #{startNum },#{endNum}

SELECT DISTINCT ct0.openId, ct0.nickName, ct0.headImgUrl FROM ddw_goodfriendplay_room_member t INNER JOIN ddw_userinfo ct0 ON t.userId = ct0.id INNER JOIN ( SELECT t1.roomId FROM ddw_goodfriendplay_room_member t1 WHERE t1.userId = #{userId} ) ct2 on ct2.roomId=t.roomId
and ct2.roomId !=#{roomId}
ORDER BY t.createTime Desc
limit #{startNum },#{endNum}
