mapper.select:
select t1.userId,t1.gameId,t1.rankId,t3.nickName,t3.headImgUrl,t3.label, t4.gradeName pgradeName,count(t2.practiceId) AS ordersCount,t6.gameName,t7.rank,IFNULL(t8.star,0)star from ddw_practice_game t1 left join ddw_practice_order t2 on t1.userId=t2.practiceId left join ddw_userinfo t3 on t1.userId = t3.id left join ddw_practice_grade t4 on t4.id=t3.practiceGradeId left join ddw_practice t5 on t5.userId=t1.userId and t5.storeId=#{storeId} left join ddw_game t6 on t6.id=t1.gameId left join ddw_rank t7 on t7.id=t1.rankId left join ddw_practice_evaluation t8 on t8.practiceId=t1.userId
where t1.appointment =#{appointment}
group by t1.userId,t1.gameId order by count(t2.practiceId) desc limit #{start},#{end}

select count(*) from ddw_practice_game t1 left join ddw_practice_order t2 on t1.userId=t2.practiceId left join ddw_userinfo t3 on t1.userId = t3.id left join ddw_practice_grade t4 on t4.id=t3.practiceGradeId left join ddw_practice t5 on t5.userId=t1.userId and t5.storeId=#{storeId} left join ddw_game t6 on t6.id=t1.gameId left join ddw_rank t7 on t7.id=t1.rankId
where t1.appointment =#{appointment}
group by t1.userId,t1.gameId

select t1.userId,t1.gameId,t1.rankId,t3.nickName,t3.headImgUrl,t3.label, t4.gradeName pgradeName,t6.gameName,t7.rank,IFNULL(t8.star,0)star from ddw_practice_game t1 left join ddw_userinfo t3 on t1.userId = t3.id left join ddw_practice_grade t4 on t4.id=t3.practiceGradeId left join ddw_practice t5 on t5.userId=t1.userId and t5.storeId=#{storeId} left join ddw_game t6 on t6.id=t1.gameId left join ddw_rank t7 on t7.id=t1.rankId left join ddw_practice_evaluation t8 on t8.practiceId=t1.userId where 1=1
and t1.appointment =#{appointment}
and t3.id NOT in
#{value}
ORDER BY t1.updateTime asc limit #{start},#{end}
