mapper.select:
SELECT DATE_FORMAT(mt.createTime,'%Y-%m-%d %H:%i') createTime, mt.type, mt.cost, mt.dealType FROM ( SELECT t1.createTime, t1.doType type, t1.doCost cost, (case when t1.doType=3 then 4 else t1.doPayStatus end) as dealType FROM ddw_order t1 WHERE (t1.doPayType = 5 or t1.doType=3) AND t1.doCustomerUserId = #{userId} UNION ALL SELECT t2.createTime, v.doType type, t2.exitCost cost, v.doPayStatus dealType
FROM ddw_order v INNER JOIN ddw_exit_order t2 ON v.id = t2.orderId WHERE v.doPayType = 5 AND v.doCustomerUserId = #{userId} UNION ALL SELECT dt.createTime, dt.type, dt.cost, 3 dealType FROM ddw_transfer dt WHERE dt.userId = #{userId} ) mt
where createTime like #{date}
ORDER BY createTime DESC
limit #{startNum },#{endNum}
